var a = [1,2,3]
var b = [4,5,6]

var fu = require('../')

function concat() {
  var i = -1, l = arguments.length, n = 0, j = 0, lx = 0, r
  while (++i < l) n += arguments[i].length
  r = Array(n), i = -1, n = 0
  while (++i < l)
    for (j = 0, lx = arguments[i].length; j < lx; j++)
      r[n++] = arguments[i][j]
  return r
}

function concat2(a, b) {
  var l1 = a.length, l2 = b.length, r = Array(l1 + l2), i = -1, n = 0
  while (++i < l1) r[n++] = a[i]
  i = -1
  while (++i < l2) r[n++] = b[i]
  return r
}

require('./')({
  'fu': function () { return concat(a, b, a, b, a, b, a, b, a, b) },
  'js': function () { return a.concat(b, a, b, a, b, a, b, a, b) }
})
