var assert = require('assert')
var bench = require('bench')
var fu = require('../')

module.exports = function (tests) {
  fu.foldl(function (a, b) {
    assert.deepEqual(a[1](), b[1]())
    return a
  }, fu.intoArray(tests))

  bench.compare(tests, 4, 1000, 1, function (er, data) { bench.show(data) })
}
